import React, { useEffect, useState } from "react";
import axios from "axios";

const TransactionHistory = () => {
  const [transactions, setTransactions] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const [accountId, setAccountId] = useState("");

  useEffect(() => {
    // Fetch user, then accounts
    let user = null;
    try {
      const userJson = localStorage.getItem("user");
      if (userJson && userJson !== "undefined") user = JSON.parse(userJson);
    } catch { user = null; }
    if (!user) return;

    (async () => {
      const res = await axios.get(`http://localhost:5000/api/accounts/user/${user.id}`);
      setAccounts(res.data);
      // Preselect first account if available
      if (res.data.length > 0) setAccountId(res.data[0].account_id);
    })();
  }, []);

  useEffect(() => {
    if (!accountId) return;
    const fetchTransactions = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/transactions/history/${accountId}`
        );
        setTransactions(response.data);
      } catch (error) {
        console.error("Error fetching transactions:", error);
      }
    };
    fetchTransactions();
  }, [accountId]);

  return (
    <div className="container">
      <h2>ðŸ§¾ Transaction History</h2>
      {/* Account selector */}
      <select
        value={accountId}
        onChange={e => setAccountId(e.target.value)}
        style={{ marginBottom: 16 }}
      >
        {accounts.map(acc => (
          <option key={acc.account_id} value={acc.account_id}>
            {acc.account_number} (ID: {acc.account_id})
          </option>
        ))}
      </select>
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Type</th>
            <th>Amount</th>
            <th>Sender Account</th>
            <th>Receiver Account</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((t, index) => (
            <tr key={index}>
              <td>{t.transaction_type}</td>
              <td>{t.amount}</td>
              <td>{t.from_account || "-"}</td>
              <td>{t.to_account || "-"}</td>
              <td>{new Date(t.transaction_date).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TransactionHistory;
