import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [accountType, setAccountType] = useState("Savings");
  const [openingBalance, setOpeningBalance] = useState("");
  const [msg, setMsg] = useState("");

  // Load user from localStorage
  let user = null;
  try {
    const userJson = localStorage.getItem("user");
    if (userJson && userJson !== "undefined") user = JSON.parse(userJson);
  } catch { user = null; }

  // Fetch accounts for current user
  useEffect(() => {
    async function fetchAccounts() {
      if (!user || !user.id) return;
      try {
        const res = await axios.get(`http://localhost:5000/api/accounts/user/${user.id}`);
        setAccounts(res.data);
      } catch (err) {
        setAccounts([]);
      }
      setLoading(false);
    }
    fetchAccounts();
  }, [user]);

  // Create new account handler (with opening balance)
  const handleCreateAccount = async (e) => {
    e.preventDefault();
    if (!user || !user.id) return;
    try {
      await axios.post("http://localhost:5000/api/accounts/create", {
        userId: user.id,
        accountType,
        openingBalance: openingBalance ? openingBalance : 0.00
      });
      setMsg("Account created!");
      setOpeningBalance("");
      // Refresh accounts list
      const res = await axios.get(`http://localhost:5000/api/accounts/user/${user.id}`);
      setAccounts(res.data);
    } catch {
      setMsg("Error creating account.");
    }
  };

  // Filter accounts: only show those with non-zero balances
  const nonZeroAccounts = accounts.filter(acc => Number(acc.balance) !== 0);

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Welcome to Your Dashboard</h1>
      <p style={styles.text}>
        Here you can view your account details, check transactions, and manage your finances.
      </p>
      <div style={styles.card}>
        <h3>Account Summary</h3>
        {loading ? (
          <p>Loading...</p>
        ) : nonZeroAccounts.length > 0 ? (
          <ul>
            {nonZeroAccounts.map(acc => (
              <li key={acc.account_id}>
                <strong>No.:</strong> {acc.account_number}<br />
                <strong>Type:</strong> {acc.account_type}<br />
                <strong>Balance:</strong> ₹{Number(acc.balance).toLocaleString()}
                <hr />
              </li>
            ))}
          </ul>
        ) : (
          <p>No account info available.</p>
        )}

        {/* Account creation form with opening balance */}
        <form onSubmit={handleCreateAccount} style={{ marginTop: 18 }}>
          <label>
            Add account:
            <select
              value={accountType}
              onChange={e => setAccountType(e.target.value)}
              style={{ marginLeft: 8, marginRight: 12 }}
            >
              <option value="Savings">Savings</option>
              <option value="Current">Current</option>
            </select>
          </label>
          <input
            type="number"
            value={openingBalance}
            placeholder="Opening Balance"
            onChange={e => setOpeningBalance(e.target.value)}
            style={{ width: 110, marginRight: 10, padding: "4px 10px" }}
            min="0"
          />
          <button type="submit" style={{ padding: "6px 16px", borderRadius: "5px" }}>
            Create
          </button>
        </form>
        {msg && <div style={{ marginTop: "8px" }}>{msg}</div>}
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: "100vh",
    backgroundColor: "#f0f4ff",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
  },
  heading: { color: "#333" },
  text: { margin: "10px 0 20px", color: "#555" },
  card: {
    backgroundColor: "white",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
    width: "350px",
    textAlign: "left",
  },
};

export default Dashboard;
