import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [account, setAccount] = useState(null);
  const [loading, setLoading] = useState(true);

  // Get user from localStorage
  let user = null;
  try {
    const userJson = localStorage.getItem("user");
    if (userJson && userJson !== "undefined") {
      user = JSON.parse(userJson);
    }
  } catch {
    user = null;
  }

  useEffect(() => {
    async function fetchAccount() {
      if (!user || !user.id) return;
      try {
        const response = await axios.get(
          `http://localhost:5000/api/accounts/${user.id}`
        );
        setAccount(response.data);
      } catch (err) {
        setAccount(null);
      }
      setLoading(false);
    }
    fetchAccount();
  }, [user]);

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Welcome to Your Dashboard</h1>
      <p style={styles.text}>
        Here you can view your account details, check transactions, and manage your finances.
      </p>
      <div style={styles.card}>
        <h3>Account Summary</h3>
        {loading ? (
          <p>Loading...</p>
        ) : account ? (
          <>
            <p>
              <strong>Account Type:</strong> {account.accountType}
            </p>
            <p>
              <strong>Balance:</strong> â‚¹{account.balance.toLocaleString()}
            </p>
          </>
        ) : (
          <p>No account info available.</p>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: "100vh",
    backgroundColor: "#f0f4ff",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
  },
  heading: {
    color: "#333",
  },
  text: {
    margin: "10px 0 20px",
    color: "#555",
  },
  card: {
    backgroundColor: "white",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
    width: "300px",
    textAlign: "left",
  },
};

export default Dashboard;
